      { name: "Installation", href: "#" }
    ],
    "Entreprise": [
      { name: "Notre histoire", href: "#" },
      { name: "Carri√®res", href: "#" },
      { name: "Presse", href: "#" },
      { name: "Blog", href: "#" },
      { name: "√âv√©nements", href: "#" }
    ]
  };

  const socialLinks = [
    {
      name: "Facebook",
      icon: "üìò",
      href: "https://www.facebook.com/",
      color: "hover:text-blue-400"
    },
    {
      name: "Instagram",
      icon: "üì∑",
      href: "https://www.instagram.com/",
      color: "hover:text-pink-400"
    },
    {
      name: "Twitter",
      icon: "üê¶",
      href: "https://twitter.com/",
      color: "hover:text-blue-300"
    },
    {
      name: "LinkedIn",
      icon: "üíº",
      href: "https://www.linkedin.com/",
      color: "hover:text-blue-500"
    },
    {
      name: "YouTube",
      icon: "üì∫",
      href: "https://www.youtube.com/",
      color: "hover:text-red-400"
    }
  ];

  const addresses = {
    "D√©partement du Sud": {
      street: "73 Rue Nicolas Geffrard",
      postalCode: "HT2345",
      city: "Arrondissement des Cayes",
      country: "Ha√Øti",
      mapLink: "https://maps.app.goo.gl/CJThiJHgriBKh1sd8",
      phone: "+(509) 4776 39 92",
      email: "sud@michelcell.ht"
    },
    "D√©partement du Nord": {
      street: "Rue 14, & 15 I",
      postalCode: "HT1110",
      city: "Cap-Ha√Øtien",
      country: "Ha√Øti",
      mapLink: "https://maps.app.goo.gl/8sFk3hG5bV9Rx7QZ8",
      phone: "+(509) 3864 84 59",
      email: "nord@michelcell.ht"
    }
  };

  const paymentMethods = ["üí≥", "üì±", "üçè", "üëõ", "üí∞", "üí∏"];

  const AddressCard = ({ title, address }) => (
    <div className="bg-white/5 backdrop-blur-sm border border-white/10 rounded-2xl p-6 hover:bg-white/10 transition-all duration-300">
      <h4 className="font-sans font-bold text-white text-lg mb-4">
        {title}
      </h4>
      <div className="space-y-3">
        <div className="flex items-start gap-3">
          <span className="text-green-400 w-6 h-6 flex items-center justify-center">
            <img src="/icon/location-map-navigation-pin-svgrepo-com.svg" alt="" srcset="" />
          </span>
          <div>
            <p className="font-sans text-gray-300 text-sm">{address.street}</p>
            <p className="font-sans text-gray-300 text-sm">
              {address.postalCode} {address.city}
            </p>
            <p className="font-sans text-gray-300 text-sm">{address.country}</p>
          </div>
        </div>

        <div className="flex items-center gap-3">
          <span className="text-green-400 w-6 h-6 flex items-center justify-center">
            <img src="/icon/phone-svgrepo-com.svg" alt="" srcset="" />
          </span>
          <a 
            href={`tel:${address.phone}`}
            className="font-sans text-gray-300 text-sm hover:text-green-400 transition-colors duration-300"
          >
            {address.phone}
          </a>
        </div>

        <div className="flex items-center gap-3">
          <span className="text-purple-400 w-6 h-6 flex items-center justify-center">
            <img src="/icon/email-letter-mail-message-svgrepo-com.svg" alt="" srcset="" />
          </span>
          <a 
            href={`mailto:${address.email}`}
            className="font-sans text-gray-300 text-sm hover:text-purple-400 transition-colors duration-300"
          >
            {address.email}
          </a>
        </div>

        <a
          href={address.mapLink}
          target="_blank"
          rel="noopener noreferrer"
          className="inline-flex items-center gap-2 font-sans text-blue-400 text-sm hover:text-blue-300 transition-colors duration-300 mt-2"
        >
          <span>üó∫Ô∏è</span>
          Voir sur la carte
        </a>
      </div>
    </div>
  );

  return (
    <footer className="bg-gradient-to-b from-gray-900 to-black border-t border-white/10 font-sans">
      {/* Newsletter Section */}
      <div className="border-b border-white/10 font-sans">
        <div className="container mx-auto px-4 py-12">
          <div className="max-w-4xl mx-auto text-center">
            <h3 className="font-sans text-2xl md:text-3xl font-bold text-white mb-4">
              Restez Connect√©
            </h3>
            <p className="font-sans text-gray-300 mb-8 max-w-2xl mx-auto">
              Recevez les derni√®res actualit√©s, promotions exclusives et conseils experts 
              directement dans votre bo√Æte mail.
            </p>

            {isSubscribed ? (
              <div className="bg-green-500/20 border border-green-500/30 rounded-2xl p-6 backdrop-blur-sm">
                <div className="text-green-400 font-mono text-lg font-bold mb-2">
                  üéâ F√©licitations !
                </div>
                <p className="font-sans text-green-300">
                  Vous √™tes maintenant inscrit √† notre newsletter.
                </p>
              </div>
            ) : (
              <form onSubmit={handleSubscribe} className="flex flex-col sm:flex-row gap-4 max-w-md mx-auto">
