  const handleSubscribe = (e) => {
    e.preventDefault();
    if (email) {
      setIsSubscribed(true);
      setEmail('');
      // Simuler l'envoi de l'email
      setTimeout(() => setIsSubscribed(false), 3000);
    }
  };

  const quickLinks = {
    "Produits": [
      { name: "Smartphones", href: "#" },
      { name: "Accessoires", href: "#" },
